package week1.day1;

public class OddOrEven {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int input=15;
			if(input%2==0)
			{
				System.out.println("The given number " +input+ " is even ");
				
			}
			else {
				System.out.println("The given number " +input+ " is odd ");
			}

	}

}
